const { chromium } = require('playwright');

(async () => {
  try {
    const browser = await chromium.connectOverCDP('http://localhost:9222');
    const context = browser.contexts()[0] || await browser.newContext();
    const page = await context.newPage();

    // Use Google search for weather
    await page.goto('https://www.google.com/search?q=weather+claremont+ca', { waitUntil: 'domcontentloaded', timeout: 30000 });

    // Wait a moment for content to load
    await page.waitForTimeout(2000);

    // Take a screenshot for debugging
    await page.screenshot({ path: '/job/workspace/tmp/weather.png' });

    // Try to extract weather info from the page
    const weatherText = await page.evaluate(() => {
      // Google weather widget selectors
      const temp = document.querySelector('#wob_tm');
      const condition = document.querySelector('#wob_dc');
      const location = document.querySelector('#wob_loc');
      const humidity = document.querySelector('#wob_hm');
      const wind = document.querySelector('#wob_ws');

      if (temp && condition) {
        return {
          location: location?.textContent || 'Claremont, CA',
          temperature: temp?.textContent + '°F',
          condition: condition?.textContent,
          humidity: humidity?.textContent,
          wind: wind?.textContent
        };
      }

      // Fallback: get all text
      return { raw: document.body.innerText.substring(0, 2000) };
    });

    console.log(JSON.stringify(weatherText, null, 2));

    await page.close();
  } catch (error) {
    console.error('Error:', error.message);
  } finally {
    process.exit(0);
  }
})();
