cat << 'EOF' > /job/workspace/tmp/weather_format.js
const weatherCodes = {
  0: "Clear sky",
  1: "Mainly clear",
  2: "Partly cloudy", 
  3: "Overcast",
  45: "Foggy",
  48: "Depositing rime fog",
  51: "Light drizzle",
  53: "Moderate drizzle",
  55: "Dense drizzle",
  61: "Slight rain",
  63: "Moderate rain",
  65: "Heavy rain",
  71: "Slight snow",
  73: "Moderate snow",
  75: "Heavy snow",
  80: "Slight rain showers",
  81: "Moderate rain showers",
  82: "Violent rain showers",
  95: "Thunderstorm"
};

const data = {
  "current": {
    "time": "2026-02-03T21:00",
    "temperature_2m": 64.3,
    "relative_humidity_2m": 31,
    "weather_code": 0,
    "wind_speed_10m": 4.9
  }
};

const condition = weatherCodes[data.current.weather_code] || "Unknown";
const localTime = new Date(data.current.time).toLocaleString('en-US', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric',
  hour: 'numeric',
  minute: '2-digit',
  timeZone: 'America/Los_Angeles'
});

const output = `Claremont, CA Weather
=====================
As of: ${localTime} (Pacific Time)

Current Conditions: ${condition}
Temperature: ${data.current.temperature_2m}°F
Humidity: ${data.current.relative_humidity_2m}%
Wind Speed: ${data.current.wind_speed_10m} mph
`;

console.log(output);
process.exit(0);
EOF
node /job/workspace/tmp/weather_format.js
